#!/usr/bin/perl my %dict_ = (); $dict-&gt;{&#39;hello&#39;}-&gt;{&#39;world&#39;} = &#39;PERL&#39;; print &#34;dict_ : $dict 
&#34;; print &#34;hello : $dict-&gt;{&#39;hello&#39;} 
